import { createStore, applyMiddleware, combineReducers } from 'redux';
import thunk from 'redux-thunk';
import logger from 'redux-logger';

// import { createStore, applyMiddleware } from '../plugins/KRedux';
// import thunk from '../plugins/thunk';
// import logger from '../plugins/logger';

function counterRedurce(state = 0, action) {
    let num = action.ployed || 1;
    switch(action.type) {
        case 'ADD':
            return { count: state.count + num };
        case 'MINUS':
            return { count: state.count - num };
        default:
            return state;
    }
}

const store = createStore(combineReducers({ count: counterRedurce }), applyMiddleware(logger, thunk));

export default store;
