// vode => dom
// render 接收vnode和传入的插入父节点 返回真实dom
// 实现转化type类型，string， function， class， component

// 虚拟dom转化为真实dom
function render(vnode, container) {

    // vnodeTree => domTree
    const node = _createNode(vnode);

    container.appendChild(node);
}

// 创建节点
function _createNode(vnode) {
    const { type, children } = vnode;
    let node;
    // 如果vnode是元素节点
    if (type === 'Text') {
        node = document.createTextNode('');
    } else {
        node = document.createElement(type);
    }

    //由于存在children，所以需要递归
    if (children.length) {
        _createChildrenNode(children, node);
    }

    // 将属性填充进节点中
    _updateNode(node, vnode);

    return node;
}

// 接收子节点vnode数组以及父节点dom
function _createChildrenNode(children, parent) {
    for(let i = 0; i < children.length; i++) {
        render(children[i], parent);
    }
}

// 接收dom以及props，进行赋值
function _updateNode(node, values) {
    Object.keys(values)
        .filter(item => item !== 'children')
        .forEach(key => {
            node[key] = values[key];
        })
}

export default {
    render
}
