// vode => dom
// render 接收vnode和传入的插入父节点 返回真实dom

function render(vnode, contarin) {

    // vnode => dom
    let node = _createNode(vnode);

    contarin.appendChild(node);
}

function _createNode(vnode) {
    const { type } = vnode;
    let node;
    // 如果vnode是元素节点
    if (type !== 'Text') {
        node = document.createTextNode('');
    } else {
        node = document.createElement(type);
    }

    return node;
}

export default {
    render
}
