import { createStore } from 'redux';

const initState = {
    count: 0
}

function reduce(state = initState, action) {
    switch(action.type) {
        case 'ADD':
            return state.count + 1;
        case 'MINUS':
            return state.count - 1;
        default:
            return state;
    }
}

const store = createStore(reduce);

export default store;
