import { createStore } from 'redux';

function reduce(state = 0, action) {
    console.log(action);
    switch(action.type) {
        case 'ADD':
            return state + 1;
        case 'MINUS':
            return state - 1;
        default:
            return state;
    }
}

const store = createStore(reduce);

export default store;
