// import { createStore } from 'redux';
import { createStore } from '../plugins/KRedux';

const initState = {
    count: 0
}

function reduce(state = initState, action) {
    switch(action.type) {
        case 'ADD':
            return { count: state.count + 1 };
        case 'MINUS':
            return { count: state.count - 1 };
        default:
            return state;
    }
}

const store = createStore(reduce);
console.log(store);

export default store;
